-- Adds dark mode theme properties to default themes

-- Up
UPDATE theme SET data = JSON_MERGE_PATCH(data, '{"vis":{"d3-maps-core":{"places":{"capital":{"label":{"fontWeight":{"normal":700}}},"general":{"label":{"color":{"normal":"#ffffff","inverted":"#525252"},"buffer":{"color":{"normal":"#323232","inverted":"#ffffff"}},"fontWeight":{"normal":400}},"symbol":{"fill":{"normal":"#ffffff","inverted":"#525252"},"stroke":{"normal":"#323232","inverted":"#ffffff"}}}}}},"style":{"filter":{"tabs":{"color":"#666","hover":{"color":"#222"},"active":{"color":"#000000","border":{"bottom":"3px solid #555555"}},"border":{"bottom":"1px solid #cccccc"}},"point":{"line":{"color":"#ccc"},"buttons":{"hover":{"background":"#ebebeb"},"active":{"border":"1px solid #888","background":"#ccc"},"border":"1px solid #ccc"}},"select":{"border":"1px solid #cccccc","textColor":"#000"}}},"overrides":[{"type":"darkMode","settings":{"colors.background":"#252525","vis.d3-maps-core.places.general.label.color.normal":"#000000","vis.d3-maps-core.places.general.symbol.fill.normal":"#000000","vis.d3-maps-core.places.general.label.color.inverted":"#e5e5e5","vis.d3-maps-core.places.general.symbol.fill.inverted":"#e5e5e5","vis.d3-maps-core.places.general.symbol.stroke.normal":"#bfbfbf","vis.d3-maps-core.places.general.label.buffer.color.normal":"#bfbfbf","vis.d3-dot-plot.grid.horizontal.baseLine.blendBaseColorWithBg":0.85}}]}') WHERE id = 'default';

UPDATE theme SET data = JSON_MERGE_PATCH(data, '{"overrides":[{"type":"darkMode","settings":{"vis.d3-maps-core.places.general.label.fontWeight.inverted":400,"vis.d3-maps-core.places.general.label.fontWeight.normal":500}}]}') WHERE id = 'datawrapper';
